import initialState from "./initialState";
import { CATEGORY, AUTHOR } from "./actiontypes";
const reducer = (state = initialState, action) => {
    switch (action.type) {
        case CATEGORY:
            return {
                ...state,
                category: action.payload,
            };
        case AUTHOR:
            return {
                ...state,
                author: action.payload,
            };
        default:
            return state;
    }
};
export default reducer;
